import Layout from '../components/Layout'
import SEO from '../components/utils/SEO'
import MyToken from '../components/Home/MyToken'
import Generate from '../components/Home/Generate'
import React from 'react'
import { useMyToken } from '../hooks/tokens'
import { useEffect } from 'react'
import { useRouter } from 'next/router'

const seoData = {
  title: 'Generator View Brain',
  description:
    'Generator Mint unique NFTs based on your ethereum address, generated by deep-learning algorithms.',
  keywords:
    'Generator,nft,defi,ai,machine learning,deep learning,ethereum,crypto,blockchain,ETH',
}

const Generator: React.FC = () => {
  const { myToken, fetching } = useMyToken()
  const router = useRouter()

  useEffect(() => {

    if(!fetching && myToken){

      router.push(`/token/${myToken.id}`)

    }

  }, [myToken, router, fetching])

  if (fetching) return <p>Loading...</p>

  return (
    <Layout>
      <SEO data={seoData} />
      Generator
      <Generate />
    </Layout>
  )
}

export default Generator
